Try{$FreeSpace = @{Expression = {[int]($_.Freespace/1GB)};Name = '| Free Space(GB)'}$PercentFree = @{Expression = {[int]($_.Freespace*100/$_.Size)}; Name = '| % of free space'}$Size = @{Expression = {[int]($_.Size/1GB)};Name = '| Size(GB)'}$VDI = Get-Content C:\TEMP\MorningChecks.txtGet-WmiObject Win32_LogicalDisk -ComputerName $VDI -Filter "Drivetype=3" |`Format-Table -Property PSComputerName,$Size, $PercentFree, $FreeSpace -AutoSize | Out-File \\LOND121718\C$\TEMP\DiskSpace.txtInvoke-Item \\LOND121718\C$\TEMP\DiskSpace.txt    }        Catch{               Write-Host "One of the Blades are offline" -foregroundcolor red               Sleep -seconds 3               }                   
