#LondonDealingRoomSupport        # File Name      : Get-ADDes.ps1        # Author         : Harrison BathFunction Show-Menu {Param([Parameter(Position=0,Mandatory=$True,HelpMessage="Enter your menu text")][ValidateNotNullOrEmpty()][string]$Menu,[Parameter(Position=1)][ValidateNotNullOrEmpty()][string]$Title="Menu",[switch]$ClearScreen)if ($ClearScreen) {Clear-Host}#build the menu prompt$menuPrompt=$title#add a return$menuprompt+="`n"#add an underline$menuprompt+="-"*$title.Length$menuprompt+="`n"#add the menu$menuPrompt+=$menuRead-Host -Prompt $menuprompt} #end function#Enter the menu below...$menu=@"    1. AD Description    ============================================    Q. QuitSelect 1 for the script or Q to exit"@#Looping and running menu until user quitsDo{        #use a Switch construct to take action depending on what menu choice is selected.            Switch (Show-Menu $menu "Harrison's Powershell Script" -clear) {            "1"{                        Try{                $COMPUTERNAME = Read-Host -Prompt "Enter Hostname"                    $description = Get-ADComputer $Computername -Properties description                        Write-Host "Hostname: $computername | Description: " $description.description -ForegroundColor Green                            Sleep -Seconds 5                }Catch                        {                            Write-Host "$COMPUTERNAME not in AD" -foregroundcolor red                                Sleep -seconds 3                        }            }        "Q"    {                #Quits Script                Write-Host "Goodbye" -ForegroundColor Green                Return               }                        Default {Write-Warning "Invalid Choice. Try again."                        sleep -seconds 2                                }                                                                    } #switch   } While ($True)
